  function toggleMode() {
    const quizSection = document.querySelector('.quiz-homepage-section');
    const isFullscreen = quizSection.classList.contains('fullscreen-mode');

    if (isFullscreen) {
      // Switch to normal mode
      quizSection.classList.remove('fullscreen-mode');
      document.body.style.overflow = 'auto';
    } else {
      // Switch to full screen mode
      quizSection.classList.add('fullscreen-mode');
      document.body.style.overflow = 'hidden';
    }
  }

