  document.getElementById('pauseBtn').addEventListener('click', togglePause);
  document.getElementById('hintBtn').addEventListener('click', showHint);
  document.getElementById('skipBtn').addEventListener('click', skipQuestion);
  document.getElementById('reviewBtn').addEventListener('click', showReview);
  document.getElementById('backToResults').addEventListener('click', function() {
    document.getElementById('qaReviewSection').style.display = 'none';
    document.getElementById('resultContainer').style.display = 'block';
  });

  // Tab buttons for leaderboard
  document.querySelectorAll('.tab-btn').forEach(btn => {
    btn.addEventListener('click', function() {
      document.querySelectorAll('.tab-btn').forEach(b => b.classList.remove('active'));
      this.classList.add('active');
      currentLeaderboardTab = this.dataset.tab;
      updateLeaderboard();
    });
  });

  // Filter buttons for Q&A review
  document.querySelectorAll('.qa-filter-btn').forEach(btn => {
    btn.addEventListener('click', function() {
      document.querySelectorAll('.qa-filter-btn').forEach(b => b.classList.remove('active'));
      this.classList.add('active');
      filterQAReview(this.dataset.filter);
    });
  });

  document.getElementById('participantName').addEventListener('keypress', function(e) {
    if (e.key === 'Enter') {
      document.getElementById('participantEmail').focus();
    }
  });

  document.getElementById('participantEmail').addEventListener('keypress', function(e) {
    if (e.key === 'Enter') {
      document.getElementById('participantPhone').focus();
    }
  });

  document.getElementById('participantPhone').addEventListener('keypress', function(e) {
    if (e.key === 'Enter') {
      startQuiz();
    }
  });

  function validateForm() {
    const name = document.getElementById('participantName').value.trim();
    const email = document.getElementById('participantEmail').value.trim();
    const phone = document.getElementById('participantPhone').value.trim();
    let isValid = true;

    if (name === '') {
      document.getElementById('nameError').textContent = '  ';
      isValid = false;
    } else if (name.length < 2) {
      document.getElementById('nameError').textContent = '  ';
      isValid = false;
    } else {
      document.getElementById('nameError').textContent = '';
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (email === '') {
      document.getElementById('emailError').textContent = '  ';
      isValid = false;
    } else if (!emailRegex.test(email)) {
      document.getElementById('emailError').textContent = '  ';
      isValid = false;
    } else {
      document.getElementById('emailError').textContent = '';
    }

    const phoneRegex = /^(\+?88)?01[3-9]\d{8}$/;
    if (phone !== '' && !phoneRegex.test(phone)) {
      document.getElementById('phoneError').textContent = '   ';
      isValid = false;
    } else {
      document.getElementById('phoneError').textContent = '';
    }

    // Get selected difficulty
    const difficultyOptions = document.querySelectorAll('input[name="difficulty"]');
    for (const option of difficultyOptions) {
      if (option.checked) {
        difficulty = option.value;
        break;
      }
    }

    return isValid;
  }

  function startQuiz() {
    if (!validateForm()) return;

    participantName = document.getElementById('participantName').value.trim();
    participantEmail = document.getElementById('participantEmail').value.trim();
    participantPhone = document.getElementById('participantPhone').value.trim();

    // Reset quiz variables
    currentQuestion = 0;
    score = 0;
    correctCount = 0;
    wrongCount = 0;
    skippedCount = 0;
    userAnswers = [];
    hintsUsed = 0;
    isPaused = false;

    document.getElementById('registrationSection').style.display = 'none';
    document.getElementById('quizContainer').style.display = 'flex';
    document.getElementById('quizRightColumn').style.display = 'flex';

    document.getElementById('welcomeMessage').textContent = `, ${participantName}!`;
    document.getElementById('highScore').textContent = highScore;

    // Filter questions based on difficulty
    let filteredQuestions = [...questions];
    if (difficulty === 'easy') {
      // Take only questions from first 3 categories
      filteredQuestions = questions.filter(q => 
        [' ', '', ''].includes(q.category)
      );
    } else if (difficulty === 'medium') {
      // Take questions from first 4 categories
      filteredQuestions = questions.filter(q => 
        [' ', '', '', ''].includes(q.category)
      );
    }
    // For hard difficulty, use all questions

    // Shuffle and limit questions based on difficulty
    filteredQuestions = shuffleArray(filteredQuestions);
    if (difficulty === 'easy') {
      filteredQuestions = filteredQuestions.slice(0, 10);
    } else if (difficulty === 'medium') {
      filteredQuestions = filteredQuestions.slice(0, 15);
    } else {
      filteredQuestions = filteredQuestions.slice(0, 20);
    }

    questions = filteredQuestions;

    updateProgress();
    loadQuestion();
  }

  function shuffleArray(array) {
    for (let i = array.length - 1; i > 0; i--) {
      const j = Math.floor(Math.random() * (i + 1));
      [array[i], array[j]] = [array[j], array[i]];
    }
    return array;
  }

  function updateProgress() {
    const progress = (currentQuestion / questions.length) * 100;
    document.getElementById('progressFill').style.width = `${progress}%`;
    document.getElementById('progressText').textContent = `${Math.round(progress)}%`;
    document.getElementById('currentScore').textContent = score;
    document.getElementById('correctAnswers').textContent = correctCount;
    document.getElementById('wrongAnswers').textContent = wrongCount;
    document.getElementById('skippedAnswers').textContent = skippedCount;
  }

  function startTimer() {
    timeLeft = 30;
    const timerText = document.getElementById('timerText');
    const timerProgress = document.getElementById('timerProgress');

    const circumference = 2 * Math.PI * 15.9155;
    timerProgress.style.strokeDasharray = `${circumference} ${circumference}`;
    timerProgress.style.strokeDashoffset = circumference;

    timerInterval = setInterval(() => {
      if (isPaused) return;

      timeLeft--;
      timerText.textContent = timeLeft;

      const offset = circumference - (timeLeft / 30) * circumference;
      timerProgress.style.strokeDashoffset = offset;

      if (timeLeft <= 10) {
        timerProgress.style.stroke = '#ff6b6b';
      } else if (timeLeft <= 20) {
        timerProgress.style.stroke = '#ffa726';
      } else {
        timerProgress.style.stroke = '#00a870';
      }

      if (timeLeft <= 0) {
        clearInterval(timerInterval);
        handleTimeUp();
      }
    }, 1000);
  }

  function loadQuestion() {
    if (currentQuestion >= questions.length) {
      showResults();
      return;
    }

    quizActive = true;
    const q = questions[currentQuestion];

    document.getElementById('questionCategory').textContent = q.category;
    document.getElementById('questionNumber').textContent = ` ${currentQuestion + 1}/${questions.length}`;
    document.getElementById('questionText').textContent = q.q;

    const optionsGrid = document.getElementById('optionsGrid');
    optionsGrid.innerHTML = '';

    const shuffledOptions = shuffleArray([...q.options]);

    shuffledOptions.forEach((option, idx) => {
      const btn = document.createElement('button');
      btn.className = 'option-btn';
      btn.textContent = option;
      btn.dataset.value = option;
      btn.onclick = () => selectAnswer(option, btn);
      optionsGrid.appendChild(btn);
    });

    document.getElementById('explanationBox').style.display = 'none';
    document.getElementById('timeBonus').style.display = 'none';

    updateProgress();
    startTimer();
  }

  function selectAnswer(selectedOption, btn) {
    if (!quizActive || isPaused) return;

    clearInterval(timerInterval);
    quizActive = false;

    const q = questions[currentQuestion];
    const correctOption = q.options[q.answer];

    // Record user answer
    userAnswers[currentQuestion] = {
      question: q.q,
      userAnswer: selectedOption,
      correctAnswer: correctOption,
      isCorrect: selectedOption === correctOption,
      isSkipped: false,
      explanation: q.explanation
    };

    //    
    document.querySelectorAll('.option-btn').forEach(opt => {
      opt.disabled = true;
      if (opt.dataset.value === correctOption) {
        opt.classList.add('correct');
      }
    });

    let timeBonus = 0;
    let showBonusMessage = false;

    // Calculate time bonus only for correct answers
    if (selectedOption === correctOption) {
      if (timeLeft > 20) {
        timeBonus = 2;
        showBonusMessage = true;
      } else if (timeLeft > 10) {
        timeBonus = 1;
        showBonusMessage = true;
      }

      // Show bonus message only for correct answers
      if (showBonusMessage) {
        document.getElementById('timeBonus').style.display = 'block';
      }

      btn.classList.add('correct');
      score += 1 + timeBonus;
      correctCount++;

      //  
      btn.style.transform = 'scale(1.05)';
      setTimeout(() => {
        btn.style.transform = 'scale(1)';
      }, 300);
    } else {
      btn.classList.add('wrong');
      wrongCount++;
      // Ensure bonus message is hidden for wrong answers
      document.getElementById('timeBonus').style.display = 'none';
    }

    //    
    setTimeout(() => {
      currentQuestion++;
      loadQuestion();
    }, 2000);

    showExplanation();
  }

  function handleTimeUp() {
    if (isPaused) return;

    quizActive = false;
    document.querySelectorAll('.option-btn').forEach(btn => btn.disabled = true);
    wrongCount++;

    // Record as skipped
    userAnswers[currentQuestion] = {
      question: questions[currentQuestion].q,
      userAnswer: null,
      correctAnswer: questions[currentQuestion].options[questions[currentQuestion].answer],
      isCorrect: false,
      isSkipped: true,
      explanation: questions[currentQuestion].explanation
    };

    setTimeout(() => {
      currentQuestion++;
      loadQuestion();
    }, 2000);

    showExplanation();
  }

  function showExplanation() {
    const box = document.getElementById('explanationBox');
    document.getElementById('explanationText').textContent = questions[currentQuestion].explanation;
    box.style.display = 'block';
  }

  function togglePause() {
    isPaused = !isPaused;
    const pauseIcon = document.querySelector('.pause-icon');
    const resumeIcon = document.querySelector('.resume-icon');

    if (isPaused) {
      pauseIcon.style.display = 'none';
      resumeIcon.style.display = 'inline';
      document.getElementById('pauseBtn').innerHTML = '<span class="pause-icon" style="display:none;"></span><span class="resume-icon"></span>  ';
    } else {
      pauseIcon.style.display = 'inline';
      resumeIcon.style.display = 'none';
      document.getElementById('pauseBtn').innerHTML = '<span class="pause-icon"></span><span class="resume-icon" style="display:none;"> </span>';
    }
  }

  function showHint() {
    if (hintsUsed >= 2 || !quizActive || isPaused) return;

    hintsUsed++;
    const q = questions[currentQuestion];
    const correctOption = q.options[q.answer];

    // Remove one wrong option
    const wrongOptions = q.options.filter(opt => opt !== correctOption);
    const optionToRemove = wrongOptions[Math.floor(Math.random() * wrongOptions.length)];

    document.querySelectorAll('.option-btn').forEach(btn => {
      if (btn.dataset.value === optionToRemove) {
        btn.disabled = true;
        btn.style.opacity = '0.5';
        btn.style.textDecoration = 'line-through';
      }
    });

    // Disable hint button after using it twice
    if (hintsUsed >= 2) {
      document.getElementById('hintBtn').disabled = true;
      document.getElementById('hintBtn').style.opacity = '0.5';
    }
  }

  function skipQuestion() {
    if (!quizActive || isPaused) return;

    clearInterval(timerInterval);
    quizActive = false;

    // Record as skipped
    userAnswers[currentQuestion] = {
      question: questions[currentQuestion].q,
      userAnswer: null,
      correctAnswer: questions[currentQuestion].options[questions[currentQuestion].answer],
      isCorrect: false,
      isSkipped: true,
      explanation: questions[currentQuestion].explanation
    };

    skippedCount++;
    updateProgress();

    setTimeout(() => {
      currentQuestion++;
      loadQuestion();
    }, 1000);
  }

  function showResults() {
    document.getElementById('questionBox').style.display = 'none';
    document.getElementById('timerContainer').style.display = 'none';
    document.getElementById('optionsGrid').style.display = 'none';
    document.getElementById('explanationBox').style.display = 'none';

    const resContainer = document.getElementById('resultContainer');
    resContainer.style.display = 'block';

    const percentage = (score / questions.length) * 100;
    let message = '';
    let performanceClass = '';

    if (percentage === 100) {
      message = '!    ! ';
      performanceClass = 'excellent';
    } else if (percentage >= 80) {
      message = '!   performance ! ';
      performanceClass = 'very-good';
    } else if (percentage >= 60) {
      message = ' !     ';
      performanceClass = 'good';
    } else if (percentage >= 40) {
      message = ' !    ';
      performanceClass = 'average';
    } else {
      message = '  ,    ';
      performanceClass = 'need-improvement';
    }

    if (score > highScore) {
      highScore = score;
      localStorage.setItem('seerahHighScore', highScore);
    }

    addToLeaderboard(participantName, score, participantEmail);

    resContainer.innerHTML = `
      <h2> , ${participantName}!</h2>
      <div class="score-display">${score}/${questions.length}</div>
      <div class="performance-message ${performanceClass}">${message}</div>
      <p><strong> :</strong> ${correctCount} | <strong> :</strong> ${wrongCount} | <strong> :</strong> ${skippedCount}</p>
      <p><strong> :</strong> ${highScore}</p>
      <p><strong>:</strong> ${participantEmail}</p>
      ${participantPhone ? `<p><strong>:</strong> ${participantPhone}</p>` : ''}
    `;

    document.getElementById('resetBtn').style.display = 'inline-block';
    document.getElementById('shareBtn').style.display = 'inline-block';
    document.getElementById('leaderboardBtn').style.display = 'inline-block';
    document.getElementById('reviewBtn').style.display = 'inline-block';
  }

  function addToLeaderboard(name, score, email) {
    const existingEntry = leaderboard.find(entry => entry.email === email);

    if (existingEntry) {
      if (score > existingEntry.score) {
        existingEntry.score = score;
        existingEntry.name = name;
        existingEntry.date = new Date().toLocaleDateString('bn-BD');
      }
    } else {
      leaderboard.push({
        name: name,
        score: score,
        email: email,
        date: new Date().toLocaleDateString('bn-BD')
      });
    }

    leaderboard.sort((a, b) => b.score - a.score);
    leaderboard = leaderboard.slice(0, 10);
    localStorage.setItem('seerahLeaderboard', JSON.stringify(leaderboard));
  }

  function showLeaderboard() {
    document.getElementById('quizContainer').style.display = 'none';
    document.getElementById('quizRightColumn').style.display = 'none';
    document.getElementById('leaderboardSection').style.display = 'flex';

    updateLeaderboard();
  }

  function updateLeaderboard() {
    const leaderboardList = document.getElementById('leaderboardList');
    leaderboardList.innerHTML = '';

    if (leaderboard.length === 0) {
      leaderboardList.innerHTML = '<p>     !</p>';
      return;
    }

    // Filter leaderboard based on current tab
    let filteredLeaderboard = [...leaderboard];
    if (currentLeaderboardTab === 'daily') {
      const today = new Date().toLocaleDateString('bn-BD');
      filteredLeaderboard = leaderboard.filter(entry => entry.date === today);
    } else if (currentLeaderboardTab === 'weekly') {
      const oneWeekAgo = new Date();
      oneWeekAgo.setDate(oneWeekAgo.getDate() - 7);
      filteredLeaderboard = leaderboard.filter(entry => {
        const entryDate = new Date(entry.date);
        return entryDate >= oneWeekAgo;
      });
    }
    // For alltime, use all entries

    filteredLeaderboard.forEach((entry, index) => {
      const item = document.createElement('div');
      item.className = 'leaderboard-item';
      item.innerHTML = `
        <div class="leaderboard-rank">${index + 1}</div>
        <div class="leaderboard-name">${entry.name}</div>
        <div class="leaderboard-score">${entry.score}</div>
      `;
      leaderboardList.appendChild(item);
    });
  }

  function showReview() {
    document.getElementById('resultContainer').style.display = 'none';
    document.getElementById('qaReviewSection').style.display = 'flex';

    displayQAReview('all');
  }

  function displayQAReview(filter) {
    const qaList = document.getElementById('qaList');
    qaList.innerHTML = '';

    let filteredAnswers = [...userAnswers];

    if (filter === 'correct') {
      filteredAnswers = userAnswers.filter(answer => answer.isCorrect);
    } else if (filter === 'wrong') {
      filteredAnswers = userAnswers.filter(answer => !answer.isCorrect && !answer.isSkipped);
    } else if (filter === 'skipped') {
      filteredAnswers = userAnswers.filter(answer => answer.isSkipped);
    }

    if (filteredAnswers.length === 0) {
      qaList.innerHTML = '<p>    </p>';
      return;
    }

    filteredAnswers.forEach((answer, index) => {
      const originalIndex = userAnswers.indexOf(answer);
      const item = document.createElement('div');
      item.className = 'qa-item';

      let statusClass = '';
      let statusText = '';

      if (answer.isCorrect) {
        statusClass = 'correct';
        statusText = '';
      } else if (answer.isSkipped) {
        statusClass = 'skipped';
        statusText = '';
      } else {
        statusClass = 'wrong';
        statusText = '';
      }

      item.innerHTML = `
        <div class="qa-item-header">
          <div class="qa-item-number"> ${originalIndex + 1}</div>
          <div class="qa-item-status ${statusClass}">${statusText}</div>
        </div>
        <div class="qa-item-question">${answer.question}</div>
        <div class="qa-item-answers">
          ${answer.userAnswer ? `<div class="qa-item-answer qa-item-user-answer"> : ${answer.userAnswer}</div>` : ''}
          <div class="qa-item-answer qa-item-correct-answer"> : ${answer.correctAnswer}</div>
        </div>
        <div class="qa-item-explanation">${answer.explanation}</div>
      `;

      qaList.appendChild(item);
    });
  }

  function filterQAReview(filter) {
    displayQAReview(filter);
  }

  function restartQuiz() {
    currentQuestion = 0;
    score = 0;
    correctCount = 0;
    wrongCount = 0;
    skippedCount = 0;

    document.getElementById('questionBox').style.display = 'block';
    document.getElementById('timerContainer').style.display = 'flex';
    document.getElementById('optionsGrid').style.display = 'grid';
    document.getElementById('explanationBox').style.display = 'none';

    document.getElementById('resultContainer').style.display = 'none';
    document.getElementById('resetBtn').style.display = 'none';
    document.getElementById('shareBtn').style.display = 'none';
    document.getElementById('leaderboardBtn').style.display = 'none';
    document.getElementById('reviewBtn').style.display = 'none';

    questions = shuffleArray(questions);

    updateProgress();
    loadQuestion();
  }

  function shareResults() {
    const shareText = `   ${score}/${questions.length}  ! ${participantName}   `;

    if (navigator.share) {
      navigator.share({
        title: '  ',
        text: shareText,
        url: window.location.href
      });
    } else {
      navigator.clipboard.writeText(shareText).then(() => {
        alert('   !      ');
      });
    }
  }
